import React, { useState, useEffect } from "react";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import DeleteIcon from "@material-ui/icons/Delete";
import Tooltip from "@material-ui/core/Tooltip";
import EditBookDialog from './EditBookDialog'
import TextField from '@material-ui/core/TextField'
import './Books.css'

export const Books = () => {
    const [books, setBooks] = useState([]);
    const [filteredBooks, setFilteredBooks] = useState([]);
    const [selectedBook, setSelectedBook]=useState(null)
    const [isDialogOpen, setIsDialogOpen]=useState(false)
    const [searchParam, setSearchParam]=useState('')
    
    useEffect(async() => {
        const res = await fetch('http://localhost:8080/elibrary/book', {
            method: 'GET',
            headers: {
              'Content-type': 'application/json',
              'Accept': 'application/json',
              'Access-Control-Allow-Headers': '*',
              'Access-Control-Allow-Credentials': 'true',
              'Access-Control-Allow-Methods': 'GET, HEAD, OPTIONS, POST, PUT',
              'Access-Control-Allow-Origin': 'http://localhost:3000/',
            }
        })
      setBooks(res)
      setFilteredBooks(res)
    }, []);
  
    const deleteBook = async(id) => (event) => {
        fetch(`http://localhost:8080/elibrary/book/${id}/delete`, {
            method: 'DELETE',
            headers: {
              'Content-type': 'application/json',
              'Accept': 'application/json',
              'Access-Control-Allow-Headers': '*',
              'Access-Control-Allow-Credentials': 'true',
              'Access-Control-Allow-Methods': 'GET, HEAD, OPTIONS, POST, PUT',
              'Access-Control-Allow-Origin': 'http://localhost:3000/',
            }
        })
      event.stopPropagation();
      const filteredArray = books.filter((element) => element.id !== id);

      const res = fetch('http://localhost:8080/elibrary/book', {
        method: 'GET',
        headers: {
          'Content-type': 'application/json',
          'Accept': 'application/json',
          'Access-Control-Allow-Headers': '*',
          'Access-Control-Allow-Credentials': 'true',
          'Access-Control-Allow-Methods': 'GET, HEAD, OPTIONS, POST, PUT',
          'Access-Control-Allow-Origin': 'http://localhost:3000/',
        }
    })
  setBooks(res)
      setBooks(filteredArray);
    };
  
    const openEditDialog =  (id) => (event) => {
      setSelectedBook(id)
      setIsDialogOpen(true)
    }
  
    const handleClose = ()=>{
      setIsDialogOpen(false)
    }
  
    const handleSaveAndClose = (book)=>{
      updateUser(book)
      setIsDialogOpen(false)
    }
  
    const updateUser=(book)=>{
      const booksAray = books;
      const bookToEdit = booksAray.find(x=> x.id===book.id)
      bookToEdit.name= book.name
      bookToEdit.writer=book.writer
      bookToEdit.genre=book.genre
      
      setBooks(booksAray)
    }
  
    const handleChange=(event)=>{
      setSearchParam(event.target.value)
    }
  
    useEffect(() => {
      if (books.length > 0) {
        const filteredUsers = books.filter((x) => x.name.includes(searchParam));
        setFilteredBooks(filteredUsers);
      }
    }, [searchParam]);
    return (
        <div className="usersComponent">
        <div className="usersList">
        <TextField label="Search" variant="outlined" value={searchParam} onChange={handleChange}/>
          <TableContainer component={Paper}>
            <Table aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell>Name</TableCell>
                  <TableCell>Writer</TableCell>
                  <TableCell>Genre</TableCell>
                  <TableCell></TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {filteredBooks.map((row, index) => (
                  <TableRow key={index} onClick={openEditDialog(row)} classes={{root:"tableRow"}}>
                    <TableCell>{row.name}</TableCell>
                    <TableCell>{row.writer}</TableCell>
                    <TableCell>{row.genre}</TableCell>
                    <TableCell>
                      <Tooltip title="Delete book" placement="top" arrow={true}>
                        <DeleteIcon
                          className="actionIcon"
                          onClick={deleteBook(row.id)}
                        />
                      </Tooltip>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </div>
        {selectedBook && <EditUserDialog open={isDialogOpen} handleClose={handleClose} selectedBook={selectedBook} handleSaveAndClose={handleSaveAndClose}/>}
      </div>
    )
}
