import react, { useState } from 'react'
import './App.css';
import UserStore from './stores/UserStore'
import { Header } from './components/Header'
import { MainWindow } from './components/MainWindow'

function App() {
  const {user, setUser} = useState([]);

  const onLogin = async(username, password) => {
    const res = await fetch('http://localhost:8080/elibrary/user/login', {
      method: 'POST',
      headers: {
        'Content-type': 'application/json'
      },
      body:
      {
          "name" : `${username}`,
          "password" : `${username}`
      }
    })

    const data = await res.json()
    setUser(data);

  }

  const onRegistration = async(registrationData) => {
    const res = await fetch('http://localhost:8080/elibrary/user/register', {
      method: 'POST',
      headers: {
        'Content-type': 'application/json'
      },
      body: JSON.stringify(registrationData)
    })

    const data = await res.json()
    setUser(data);

  }

  return (
    <>
      <Header />
      <div className='body'>
        <MainWindow onRegistration={onRegistration} />
      </div>
    </>
  );
}

export default App;
