import react, { useState } from 'react'
import Session from "react-session-api";
import "./MainWindow.css";
import UserStore from "../stores/UserStore";
import { Login } from "./Login";
import { Registration } from "./Registration";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

export const MainWindow = ({ onLogin, onRegistration }) => {
    const [name, setName] = useState(localStorage.getItem('name'))

    const logout = () => {
        localStorage.setItem('name', '');
        localStorage.setItem('admin', false);
    }

  return (
    <div>
      <Router>
        <div>
          <nav>
            <ul> 
              <li>
                <Link to="/">Books</Link>
              </li>
              <li>
                <Link to="/login">Login</Link>
              </li>
              <li>
                <Link to="/register">Register</Link>
              </li>
              <li>
                <Link to="/logout">Logout</Link>
              </li>
            </ul>
          </nav>
          <Switch>
            <Route path="/" exact>
            {name != '' ? 'you are logged in' : 'you need to log in first'}
            </Route>
            <Route path="/login"> 
              {name != '' ? <Login onLogin={onLogin} /> : 'You are already logged in'}
            </Route>
            <Route path="/register">
              {name != '' ? <Registration onRegistration={onRegistration}/> : 'You are already logged in'}
            </Route>
            <Route path="/logout">
              {name != '' ? logout() : 'You arent logged in'}
            </Route>
          </Switch>
        </div>
      </Router>
    </div>
  );
};
