import React, { useState } from "react";
import { TextField, Checkbox } from '@material-ui/core';
import './Registration.css'

export const Registration = ({ onRegistration }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [admin, setAdmin] = useState(false);

  const onSubmit = (e) => {
    e.preventDefault();

    if (!username) {
      alert("Please add a name");
      return;
    }

    if (!password) {
      alert("Please add a password");
      return;
    }

    onRegistration({ username, password, admin });

    setUsername("");
    setPassword("");
    setAdmin(false);
  };
  return (
    <div>
      <form className="login-form" onSubmit={onSubmit}>
        <div className="form-control">
          <TextField id="standard-basic" label="Username" type='text' value={username} onChange={(e) => setUsername(e.target.value)} />
        </div>
        <div className="form-control">
        <TextField id="standard-basic" label="Password" type='password' value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <div className="form-control">
        <Checkbox id="standard-basic" label="Admin" value={admin} onChange={(e) => setAdmin(e.target.value)} />
        </div>
        <input type="submit" value="login" className="btn btn-block" className='submit-btn' />
      </form>
    </div>
  );
};
